(1757, 'HBRW', 'HBRW-1113', '1', '3', 'Elementary Hebrew I', 'An introductory course in the principles of biblical Hebrew with special emphasis on verbal forms. This course is normally offered in the fall semester.'),
(1758, 'HBRW', 'HBRW-1213', '1', '3', 'Elementary Hebrew II', 'Prerequisite: HBRW-3113. A continuation of HBRW-3113. This course will include readings in Hebrew narratives and an introduction to textual criticism and Hebrew exegesis. This course is normally offered in the spring semester.'),
(1759, 'HBRW', 'HBRW-2113', '2', '3', 'Intermediate Hebrew I', 'Pre-requisite: HBRW-1213 An intermediate course in the principles of Biblical Hebrew with special emphasis on reading and dating of narrative texts. This course is normally offered in the fall semester.'),
(1760, 'HBRW', 'HBRW-2213', '2', '3', 'Intermediate Hebrew II', 'Pre-requisite: HBRW-1213 An intermediate course in the principles of Biblical Hebrew with a focus on Biblical Hebrew poetry. This course is normally offered in the spring semester.'),
(1761, 'HBRW', 'HBRW-3113', '3', '3', 'Advanced Hebrew I', 'Pre-requisite: HBRW-2213 An advanced course in the principles of Biblical Hebrew that includes an introduction to Biblical Aramaic. This course is normally offered in the fall semester.'),
(1762, 'HBRW', 'HBRW-3213', '3', '3', 'Advanced Hebrew II', 'Pre-requisite: HBRW-2213 An advanced course in the principles of Biblical Hebrew that introduces the student to the reading of unpointed Hebrew texts spanning from the iron age to the rabbinic period. This course is normally offered in the fall semester.'),
(1763, 'HBRW', 'HBRW-4813', '4', '3', 'SS: Hebrew', ''),
